component_architecture.state_management:
interface ConversationStore {
  // Session Management
  sessionId: string | null;
  phase: ConversationPhase;
  isConnected: boolean;

  // Conversation State
  messages: Message[];
  isTyping: boolean;
  context: ConversationContext;

  // OKR State
  objective: ObjectiveDraft | null;
  keyResults: KeyResultDraft[];
  qualityScores: QualityScores;

  // UI State
  sidebarOpen: boolean;
  exportFormat: ExportFormat;
  theme: 'light' | 'dark' | 'system';

  // Actions
  sendMessage: (content: string) => Promise;
  updatePhase: (phase: ConversationPhase) => void;
  updateOKR: (okr: OKRDraft) => void;
  exportOKRs: (format: ExportFormat) => void;
  resetSession: () => Promise;
}

websocket_integration.connection_management:
class WebSocketClient {
  private socket: Socket;
  private reconnectAttempts: number = 0;
  private maxReconnectAttempts: number = 5;

  connect(sessionId: string): void;
  disconnect(): void;
  sendMessage(message: string): Promise;
  private handleReconnection(): void;
  private handleError(error: Error): void;
}

websocket_integration.event_handling:
// WebSocket event handlers
socket.on('receive-message', (data: MessageResponse) => {
  addMessage(data.message);
  updateQualityScores(data.scores);
  updateOKRState(data.okrs);
  setTyping(false);
});

socket.on('typing', (data: TypingIndicator) => {
  setTyping(data.isTyping);
});

socket.on('session-update', (data: SessionUpdate) => {
  updateSessionState(data);
});

socket.on('error', (error: SocketError) => {
  handleSocketError(error);
});

